export const rgb_to_hex = (r, g, b) => {
	if (r === null || g === null || b === null) {
		return RGBToHex(0, 0, 0);
	}
	r = r.toString(16);
	g = g.toString(16);
	b = b.toString(16);

	if (r.length == 1) r = "0" + r;
	if (g.length == 1) g = "0" + g;
	if (b.length == 1) b = "0" + b;

	return "#" + r + g + b;
};

export const lerp = (a, b, t) => a * (1 - t) + b * t;

export const lerp_color = (a, b, t) => {
	const ar = a >> 16,
		ag = (a >> 8) & 0xff,
		ab = a & 0xff,
		br = b >> 16,
		bg = (b >> 8) & 0xff,
		bb = b & 0xff,
		rr = ar + t * (br - ar),
		rg = ag + t * (bg - ag),
		rb = ab + t * (bb - ab);

	return (rr << 16) + (rg << 8) + (rb | 0);
};
